import React, { useState, useEffect } from 'react';
import './flashcard.css';
import wordsData from './wordsFlash.json';
import { useSwipeable } from 'react-swipeable';

const Flashcard = () => {
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [selectedUnits, setSelectedUnits] = useState([1]); // Varsayılan olarak birinci üniteyi göster

  useEffect(() => {
    setWords(
      wordsData.filter(word => selectedUnits.includes(word.unit)) // Seçilen ünitelere göre kelimeleri filtrele
    );
  }, [selectedUnits]);

  const handlers = useSwipeable({
    onSwipedLeft: () => handleSwipeLeft(),
    onSwipedRight: () => handleSwipeRight(),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  const handleSwipeRight = () => {
    if (currentWordIndex < words.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handleSwipeLeft = () => {
    if (currentWordIndex < words.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handleCardClick = () => {
    setShowAnswer(!showAnswer);
  };

  const handleUnitSelect = (unit) => {
    if (selectedUnits.includes(unit)) {
      setSelectedUnits(selectedUnits.filter(selectedUnit => selectedUnit !== unit));
    } else {
      setSelectedUnits([...selectedUnits, unit]);
    }
  };

  const handleRightButtonClick = () => {
    handleSwipeRight();
  };

  return (
    <div className="flashcard-container" {...handlers}>
      <div
        className={`flashcard ${showAnswer ? 'show-answer' : ''}`}
        onClick={handleCardClick}
      >
        <div className="flashcard-content">
          <div className="flashcard-front">
            <p>{words[currentWordIndex]?.english}</p>
          </div>
          <div className="flashcard-back">
            <p>{words[currentWordIndex]?.turkish}</p>
          </div>
        </div>
      </div>
      <div className="unit-buttons">
        {wordsData.map((word, index) => (
          <button
            key={index}
            className={selectedUnits.includes(word.unit) ? 'selected' : ''}
            onClick={() => handleUnitSelect(word.unit)}
          >
            Ünite {word.unit}
          </button>
        ))}
        <button onClick={handleRightButtonClick}>Sağa Kaydır</button>
      </div>
    </div>
  );
};

export default Flashcard;
