import React, { useState, useEffect } from 'react';
import './flashcard.css';
import wordsData from './wordsFlash.json';
import { useSwipeable } from 'react-swipeable';

const Flashcard = () => {
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [selectedUnit, setSelectedUnit] = useState(null); // Seçilen üniteyi saklayacak state

  useEffect(() => {
    // Seçili ünite varsa sadece o üniteye ait kelimeleri filtrele
    if (selectedUnit !== null) {
      setWords(
        wordsData.filter(word => word.unit === selectedUnit)
      );
    } else {
      setWords(wordsData); // Seçili ünite yoksa tüm kelimeleri göster
    }
  }, [selectedUnit]);

  const handlers = useSwipeable({
    onSwipedLeft: () => handleSwipeLeft(),
    onSwipedRight: () => handleSwipeRight(),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  const handleSwipeRight = () => {
    if (currentWordIndex < words.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handleSwipeLeft = () => {
    if (currentWordIndex < words.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handleCardClick = () => {
    setShowAnswer(!showAnswer);
  };

  const handleUnitSelect = (unit) => {
    setSelectedUnit(unit === selectedUnit ? null : unit); // Butona tıklanan ünite seçiliyse iptal et, değilse seç
  };

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="flashcard-container" {...handlers}>
            <div
              className={`flashcard ${showAnswer ? 'show-answer' : ''}`}
              onClick={handleCardClick}
            >
              <div className="flashcard-content">
                <div className="flashcard-front">
                  <p>{words[currentWordIndex]?.english}</p>
                </div>
                <div className="flashcard-back">
                  <p>{words[currentWordIndex]?.turkish}</p>
                </div>
              </div>
            </div>
            <div className="unit-buttons mt-3">
              {[...new Set(wordsData.map(word => word.unit))].map((unit, index) => (
                <button
                  key={index}
                  className={`btn ${selectedUnit === unit ? 'btn-primary' : 'btn-outline-primary'} mx-1`}
                  onClick={() => handleUnitSelect(unit)}
                >
                  Ünite {unit}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Flashcard;
