import React, { useState } from 'react';
import './App.css';

const Word = () => {
  const [words, setWords] = useState([
    { id: 1, english: 'Hello', turkish: 'Merhaba' },
    { id: 2, english: 'Goodbye', turkish: 'Hoşça kal' },
    // Buraya kelime listesini ekle
  ]);

  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [unknownWords, setUnknownWords] = useState([]);

  const handleSwipeRight = () => {
    setCurrentWordIndex(currentWordIndex + 1);
  };

  const handleSwipeLeft = () => {
    const word = words[currentWordIndex];
    setUnknownWords([...unknownWords, word]);
    setCurrentWordIndex(currentWordIndex + 1);
  };

  return (
    <div className="container">
      <div className="card text-center mt-5">
        <div className="card-body">
          <h5 className="card-title">{words[currentWordIndex].english}</h5>
          <p className="card-text">{words[currentWordIndex].turkish}</p>
        </div>
        <div className="card-footer">
          <div className="btn-group" role="group" aria-label="Basic example">
            <button
              type="button"
              className="btn btn-danger"
              onClick={handleSwipeLeft}
            >
              Bilmiyorum
            </button>
            <button
              type="button"
              className="btn btn-success"
              onClick={handleSwipeRight}
            >
              Biliyorum
            </button>
          </div>
        </div>
      </div>
      {/* Burada görüntülemek istediğin diğer içerikleri ekle */}
    </div>
  );
};

export default Word;
