import React, { Component } from 'react';
import './flashcard.css';

class Flashcard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cards: [
        {
          "english": "apple",
          "turkish": "elma"
        },
        {
          "english": "cat",
          "turkish": "kedi"
        },
        {
          "english": "book",
          "turkish": "kitap"
        },
        {
          "english": "computer",
          "turkish": "bilgisayar"
        },
        {
          "english": "sun",
          "turkish": "güneş"
        }
      ]
      ,  // JSON verilerini burada saklayabilirsiniz.
      currentCardIndex: 0,
      isFlipped: false,
    };
  }

  componentDidMount() {
    // JSON verilerini burada alabilirsiniz ve 'cards' durumunu güncelleyebilirsiniz.
  }

  handleFlipCard = () => {
    this.setState((prevState) => ({ isFlipped: !prevState.isFlipped }));
  }

  handleNextCard = () => {
    this.setState((prevState) => ({
      currentCardIndex: (prevState.currentCardIndex + 1) % prevState.cards.length,
      isFlipped: false,
    }));
  }

  handlePreviousCard = () => {
    this.setState((prevState) => ({
      currentCardIndex: (prevState.currentCardIndex - 1 + prevState.cards.length) % prevState.cards.length,
      isFlipped: false,
    }));
  }

  render() {
    const { cards, currentCardIndex, isFlipped } = this.state;
    const currentCard = cards[currentCardIndex];

    return (
      <div>
        <div className="flashcard">
          <div className={`flashcard-inner ${isFlipped ? 'flipped' : ''}`} onClick={this.handleFlipCard}>
            <div className="flashcard-front">
              {currentCard && currentCard.english}
            </div>
            <div className="flashcard-back">
              {currentCard && currentCard.turkish}
            </div>
          </div>
        </div>
        <button onClick={this.handlePreviousCard}>Önceki</button>
        <button onClick={this.handleNextCard}>Sonraki</button>
      </div>
    );
  }
}

export default Flashcard;
