import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';
import './flashcard.css';
import wordsData from './wordsFlash.json';
import { useSwipeable } from 'react-swipeable';

const Flashcard = () => {
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [selectedUnits, setSelectedUnits] = useState([]); // Birden fazla ünite seçmek için liste oluşturuldu

  useEffect(() => {
    if (selectedUnits.length > 0) {
      setWords(
        wordsData.filter(word => selectedUnits.includes(word.unit))
      );
    } else {
      setWords(wordsData);
    }
  }, [selectedUnits]);

  const handlers = useSwipeable({
    onSwipedLeft: () => handleSwipeLeft(),
    onSwipedRight: () => handleSwipeRight(),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  const handleSwipeRight = () => {
    if (currentWordIndex > 0) {
      setCurrentWordIndex(currentWordIndex - 1);
      setShowAnswer(false);
    }
  };

  const handleSwipeLeft = () => {
    if (currentWordIndex < words.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handleCardClick = () => {
    setShowAnswer(!showAnswer);
  };

  const handleUnitSelect = (unit) => {
    if (selectedUnits.includes(unit)) {
      setSelectedUnits(selectedUnits.filter(selectedUnit => selectedUnit !== unit));
    } else {
      setSelectedUnits([...selectedUnits, unit]);
    }
  };

  const handlePreviousClick = () => {
    if (currentWordIndex > 0) {
      setCurrentWordIndex(currentWordIndex - 1);
      setShowAnswer(false);
    }
  };

  const handleNextClick = () => {
    if (currentWordIndex < words.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };
  const buttonColors = ['btn-primary', 'btn-secondary', 'btn-success', 'btn-danger', 'btn-warning', 'btn-info', 'btn-light', 'btn-dark'];

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="unit-buttons mt-3 text-center">
            {[...new Set(wordsData.map(word => word.unit))].map((unit, index) => (
              <button
                key={index}
                className={`btn ${selectedUnits.includes(unit) ? buttonColors[index % buttonColors.length] : 'btn-outline-primary'} mx-1`}
                onClick={() => handleUnitSelect(unit)}
              >
                Ünite {unit}
              </button>
            ))}
          </div>
          <div className="flashcard-container mt-3" {...handlers}>
            <div
              className={`flashcard ${showAnswer ? 'show-answer' : ''}`}
              onClick={handleCardClick}
            >
              {/* ...flashcard içeriği */}
            </div>
            <div className="navigation-buttons mt-3 text-center">
              <button className="btn btn-primary mx-1" onClick={handlePreviousClick}>
                Önceki
              </button>
              <button className="btn btn-primary mx-1" onClick={handleNextClick}>
                Sonraki
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Flashcard;
