import React, { useState, useEffect } from 'react';
import './flashcard.css';
import wordsData from './wordsFlash.json'; // JSON verisini import et
import { useSwipeable } from 'react-swipeable';

const Flashcard = () => {
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [swipeDirection, setSwipeDirection] = useState(null);

  useEffect(() => {
    setWords(wordsData);
  }, []);

  const handlers = useSwipeable({
    onSwipedLeft: () => handleSwipeLeft(),
    onSwipedRight: () => handleSwipeRight(),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
    delta: 50,
  });

  const handleSwipeRight = () => {
    if (currentWordIndex < words.length - 1) {
      setSwipeDirection('right');
      setTimeout(() => {
        setCurrentWordIndex(currentWordIndex + 1);
        setShowAnswer(false);
        setSwipeDirection(null);
      }, 400); // Geçişin tamamlanacağı süre
    }
  };

  const handleSwipeLeft = () => {
    if (currentWordIndex < words.length - 1) {
      setSwipeDirection('left');
      setTimeout(() => {
        setCurrentWordIndex(currentWordIndex + 1);
        setShowAnswer(false);
        setSwipeDirection(null);
      }, 400); // Geçişin tamamlanacağı süre
    }
  };

  const handleCardClick = () => {
    setShowAnswer(!showAnswer);
  };

  const cardStyles = {
    transform: `translateX(${swipeDirection === 'right' ? '100%' : swipeDirection === 'left' ? '-100%' : '0'})`,
    transition: 'transform 0.4s ease-in-out',
  };

  return (
    <div className="flashcard-container" {...handlers}>
      <div className="flashcard" onClick={handleCardClick} style={cardStyles}>
        <div className={`flashcard-content ${showAnswer ? 'show-answer' : ''}`}>
          <div className="flashcard-front">
            <p>{words[currentWordIndex]?.english}</p>
          </div>
          <div className="flashcard-back">
            <p>{words[currentWordIndex]?.turkish}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Flashcard;
